mapper.select:
select tiku_comment.* from tiku_comment where 1=1

  and user_id=#{userId}


  and tiku_name=#{name}


  and question_id=#{questionId}

order by create_at desc

  limit #{pageStart},#{pageSize}

select count(*) from (
select tiku_comment.* from tiku_comment where 1=1

  and user_id=#{userId}


  and tiku_name=#{name}


  and question_id=#{questionId}

order by create_at desc)
